import './ExhibitElement.css';

function ExhibitElement(props) {
    return (
        <div className="row">
            <div className="image-plus-caption">
                <img className="source" src={require(props.image)} alt="display here"></img>
                <div className="caption">{props.caption}</div>
            </div>
            <p className="description">{props.description}</p>
        </div>
    );
}

export default ExhibitElement;
